% READFORT44 - Reads the geometry file of the experiment%%  M. den Toom%function [n,m,l,la,nun,xmin,xmax,ymin,ymax,hdim,x,y,z,xu,yv,zw,landm] = ...    readfort44(filename)%fprintf(1,'Reading geometry...\n')%% - OPEN FILE -fid     = fopen(filename,'r');if (fid==-1)    error('We regret to inform you that %s was not found.',filename)end%% - READ HEADER -version = fscanf(fid,'%*s %d',1);fprintf(1,'-- this is mask output version %d.\n',version)%% - READ DOMAIN SPECS -n       = fscanf(fid,'%d',1);m       = fscanf(fid,'%d',1);l       = fscanf(fid,'%d',1);la      = fscanf(fid,'%d',1);nun     = fscanf(fid,'%d',1);SLIP    = fscanf(fid,'%d',1);xmin    = fscanf(fid,'%f',1);xmax    = fscanf(fid,'%f',1);ymin    = fscanf(fid,'%f',1);ymax    = fscanf(fid,'%f',1);hdim    = fscanf(fid,'%f',1);x       = fscanf(fid,'%f',n);y       = fscanf(fid,'%f',m);z       = fscanf(fid,'%f',l);xu      = fscanf(fid,'%f',n+1);yv      = fscanf(fid,'%f',m+1);zw      = fscanf(fid,'%f',l+1);%% - READ MASK -landm   = zeros(n+2,m+2,l+la+2);for k = 1:l+la+2    for j = 1:m+2        landm(:,j,k) = fscanf(fid,'%d',n+2);    endend%% - CLOSE FILE -fclose(fid);